// "SPDX-License-Identifier: <SPDX-License>"
pragma solidity ^0.5.0;

//import other two contracts so that we can interact with them
import "./DappToken.sol";
import "./DaiToken.sol";


contract TokenFarm {
    address public owner;
    string public name = 'Dapp token farm';
    DappToken public dappToken;
    DaiToken public daiToken;

    address[] public stakers;
    mapping(address => uint) public stakingBalance;
    mapping(address => bool) public hasStaked;
    mapping(address => bool) public isStaking;

    constructor(DappToken _dappToken, DaiToken _daiToken) public {
        dappToken = _dappToken;
        daiToken = _daiToken;
        owner = msg.sender; //set the msg.sender of the contract (i.e. contract creator) as the owner, aka the one who may issue tokens
    }

    //stake tokens
    //unstake tokens
    //issue tokens

    //stake tokens (deposit)
    function stakeTokens(uint _amount) public payable {
        //require amount staked to be greater than zero, good for clean code
        require(_amount > 0, 'amount cannot be zero'); //if require condition is true, code runs, if require condition is false, code stops and exception that we specified is raised
        //code - we want this function, when called, to transfer tokens from the users wallet, into this smart contract
        daiToken.transferFrom(msg.sender, address(this), _amount);
        //update staking balance
        stakingBalance[msg.sender] = stakingBalance[msg.sender] + _amount;
        //if hasStaked = false, add msg.sender's address to stakers
        if(!hasStaked[msg.sender]) {
            stakers.push(msg.sender);
        }

        hasStaked[msg.sender] = true;
        isStaking[msg.sender] = true;

    }

    //withdraw tokens
    function withdrawTokens(uint _amount) public {
        //require the withdrawer to be a member of the stakers array
        uint balance = stakingBalance[msg.sender];
        require(balance > 0, 'withdrawer must currently be staked');

        //transfer the dai tokens back to the sender, and subtract the amount withrawn from the sender's balance
        daiToken.transfer(msg.sender, _amount);

        stakingBalance[msg.sender] = balance - _amount;

        if(balance - _amount == 0) {
        isStaking[msg.sender] = false;
        }

    }

    //issue dapp tokens in return for staking
    function issueTokens() public {
        //require that only the owner of the contract can call this function (this app has been permissioned..)
        require(msg.sender == owner, 'caller must be the owner');
        //issue tokens to stakers
        for(uint i = 0; i < stakers.length; i++) {
            address recipient = stakers[i];
            uint balance = stakingBalance[recipient];
            if(balance > 0) {
            dappToken.transfer(recipient, balance);
            }
        }
    }




    //unstake tokens (withdraw)

}
